import React from 'react'
import { Chart } from 'primereact/chart'
import moment from 'moment'
import Transactions from './transactions'
export default function trader({pair,data,store}:{pair:string, data:any ,store:IState}){
    const lastPrices = store.ticks.map((tick:ITick)=>{
        return {
            y:tick.pairs[pair].c[0],
            x:moment.unix(tick.timestamp).format("HH:mm:ss")
        }
    })
    const transactions = Object.values(store.closedTransactions)

    .filter(t=>t.descr.pair===store.assetPairs[pair]?.altname)
    .map(t=>({
            ...t.descr,
            status:t.status,
            volume:t.vol,
            cost:t.cost,
            fee:t.fee,
            time:t.opentm,
            date:moment.unix(t.opentm).format("YYYY-MM-DD HH:mm:ss")

        })
    )
    const sellData = transactions
    .filter(t=>t.type==="sell" && t.status==="closed")
    .map(t=>({
        y:t.price,
        x: moment.unix(t.time).format("HH:mm:ss")
    }) )
    const buyData = transactions
    .filter(t=>t.type==="buy" && t.status==="closed")
    .map(t=>({
        y:t.price,
        x: moment.unix(t.time).format("HH:mm:ss")
    }) )
    const sellDataExpired = transactions
    .filter(t=>t.type==="sell" && t.status==="expired")
    .map(t=>({
        y:t.price,
        x: moment.unix(t.time).format("HH:mm:ss")
    }) )
    const buyDataExpired = transactions
    .filter(t=>t.type==="buy" && t.status==="expired")
    .map(t=>({
        y:t.price,
        x: moment.unix(t.time).format("HH:mm:ss")
    }) )
    const labels = [ ...store.ticks.map(tick=>tick.timestamp), ...transactions.map(t=>t.time) ].sort().map(l=>moment.unix(l).format("HH:mm:ss"))
    const basicData = {

       labels:labels,
        datasets: [
            {
                label: 'price',
                data:lastPrices,
                fill: false,
                borderColor: '#42A5F5'
            },
            {
                label: 'buy',
                data:buyData,
                showLine:false,
                pointBorderWidth:7,
                fill: false,
                borderColor: '#10a366'
            },
            {
                label: 'sell',
                showLine:false,
                pointBorderWidth:7,
                data:sellData,
                fill: false,
                borderColor: '#a31510'
            },
            {
                label: 'expired sell',
                showLine:false,
                pointBorderWidth:1,
                data:sellDataExpired,
                fill: false,
                radius:5,

                borderStyle:'cross',
                borderColor: '#a31510'
            },
            {
                label: 'expired buy',
                showLine:false,
                pointBorderWidth:1,
                radius:5,
                data:buyDataExpired,
                fill: false,
                borderStyle:'cross',
                borderColor: '#10a366'
            },

        ]
    }
    return <>
   <Chart   type="line" data={basicData} style={{height:200}} options={{
       responsive:true,  maintainAspectRatio: false,
    animation:{
        duration:0
    }
}} />
<div className="p-d-flex">
<div className="p-mr-2"><Transactions transactions={transactions}/></div>
    <div className="p-mr-2">Item 2</div>
</div>


    </>
}
