import React from 'react'
import { Toolbar } from 'primereact/toolbar'
import { Button } from 'primereact/button'


export default function TopBar({store}:{store:IState}){
    const leftContents = ()=> (
        <React.Fragment>
            {Object.entries(store.balance).map( ([coin,value],i,{length})=><><Button type="button" label={value} className="p-button-warning" /><Button type="button" label={coin} />{i<length-1&&<i className="pi pi-bars p-toolbar-separator p-mr-2" />}</> ) }
        </React.Fragment>
    )

return <Toolbar left={leftContents} />

}
