import React, { useEffect, useReducer } from 'react'
import { Toolbar } from 'primereact/toolbar'
import { Button } from 'primereact/button'
import {fetchBalance, initialState,reducer} from './store'
import axios from 'axios'

export default function TopBar(){
    const [store, dispatch] = useReducer(reducer, initialState)
    useEffect(()=>{fetchBalance(dispatch)})
    useEffect(()=>{
        const intervalPid = setInterval(()=>fetchBalance(dispatch),10000)
        return ()=>clearInterval(intervalPid)
    })
    const leftContents = ()=> (
        <React.Fragment>
            {Object.entries(store.balance).map( ([coin,value],i,{length})=><><Button type="button" label={value} className="p-button-warning" /><Button type="button" label={coin} />{i<length-1&&<i className="pi pi-bars p-toolbar-separator p-mr-2" />}</> ) }
        </React.Fragment>
    )

return <Toolbar left={leftContents} />

}
