import { ReducerState, ReducerWithoutAction } from "react"


export interface IState {
    balance:{
        [key:string]:string
    }
}
export type IAction = 
| {
    type:'SET_BALANCE'
    payload: {
        [key:string]:string
    }
}
export const initialState:IState = {
    balance:{}
}

export const reducer=(state:IState,action:IAction):IState=>{
    if(action.type==='SET_BALANCE'){
        return {balance:action.payload as IState["balance"]}
    }
    return state
}
