import React, { useEffect, useReducer } from 'react'
import { Toolbar } from 'primereact/toolbar'
import { Button } from 'primereact/button'
import {initialState,reducer} from './store'
import axios from 'axios'

export default ()=>{
    const [store, dispatch] = useReducer(reducer, initialState)
    useEffect(()=>{
        const intervalPid = setInterval(async ()=>{
            const balance = await axios.get('/balance')
            console.log(balance.data)
            dispatch({type:"SET_BALANCE", payload:balance.data})
        },10000)
        return ()=>clearInterval(intervalPid)
    })
    const leftContents = ()=> (
        <React.Fragment>
            {Object.entries(store.balance).map( ([coin,value])=><Button label={coin} icon="pi pi-plus" className="p-mr-2" /> ) }
        </React.Fragment>
    )

return <Toolbar left={leftContents} />

}
